import Link from 'next/link';
import { getPostsQuery } from '../lib/queries';
import { graphcms } from '../lib/client';

export async function getStaticProps() {
  const { posts } = await graphcms.request(getPostsQuery);

  return {
    props: {
      posts,
    },
  };
}

const Home = ({ posts }) => {
  console.log(posts);
  return posts.map(({ title, slug }) => (
    <Link key={slug} href={`/posts/${slug}`}>
      <a>{title}</a>
    </Link>
  ));
};
export default Home;
