export const getPostQuery = `
query getPost($slug: String!) {
    post(where: {slug: $slug}) {
      title
      excerpt
      publishedAt
      slug
      tags
      seo {
        keywords
      }
      content {
        html
      }
      author {
        name
        picture {
          authorAvatar {
            picture {
              url
            }
          }
        }
      }
    }
  }
`;

export const getPostsQuery = `
query getPostsList {
    posts {
      title
      slug
    }
  }
`;
